import pandas as pd
import matplotlib.pyplot as plt
from scipy.stats import linregress
import numpy as np
def draw_plot():
    # Read data from file
    df = pd.read_csv('epa-sea-level.csv')
    pd.options.display.float_format = '{:,.16f}'.format
    # Create scatter plot
    fig, axs = plt.subplots(1)
# plt.scatter(df['Year'],df['CSIRO Adjusted Sea Level'],c="red")
    plt.scatter(df['Year'],df['CSIRO Adjusted Sea Level'])

    # Create first line of best fit
    x=df['Year']
    y=np.array(df['CSIRO Adjusted Sea Level'],dtype=float)
    result = linregress(x, y)
    slope = result.slope
    intercept = result.intercept
    ll=[ i for i in range(2014,2051)]
    x2={'Year':ll}

    x2=pd.DataFrame(x2)
    # x2

    x2=pd.concat([pd.DataFrame(x),x2],axis=0).reset_index()
    # x2
    x2=x2['Year']
    y_hat = slope*x2 + intercept
    # y_hat =np.array(y_hat)
    y_hat =np.array(y_hat,dtype='float64')
    # print(y_hat)
    # plt.scatter(x,y,color="green")
    plt.plot(x2,y_hat,color="red")
   
    # Create second line of best fit
    df2=df.query(" Year >=2000")
# x3.head()
    x3=df2['Year']
    y3=df2['CSIRO Adjusted Sea Level']
  

    result2 = linregress(x3, y3)
    slope2 = result2.slope
    intercept2 = result2.intercept
    
    x22={'Year':ll}

    x22=pd.DataFrame(x22)
# # x2

    x22=pd.concat([pd.DataFrame(x3),x22],axis=0).reset_index()
# # x2
    x22=x22['Year']
    y_hat2 = slope2*x22 + intercept2
    y_hat2 =np.array(y_hat2)
    # plt.scatter(x,y,color="green")
    plt.plot(x22,y_hat2,color="green")

# plt.show()

    # Add labels and title
    plt.xlabel("Year")
    plt.ylabel("Sea Level (inches)")
    plt.title("Rise in Sea Level")
    
    # Save plot and return data for testing (DO NOT MODIFY)
    plt.savefig('sea_level_plot.png')
    return plt.gca()
